    public function data()
    {
        $idklinik = auth()->user()->id_klinik;
        $tindakan = Tindakan::where('tindakan.id_klinik', $idklinik)
                    ->orderBy('tindakan.id', 'asc')->get();

        return Datatables::of($tindakan)
            ->addIndexColumn()
            ->addColumn('action', function($tindakan){
                return '
                <div class="action-buttons">
                    <center>
                        <button onClick="editFormTindakan(`'. route('tindakan.update', $tindakan->id) . '`)" class="btn btn-xs btn-default btn-rounded rounded-lg text-warning pl-2 pr-2" data-toggle="tooltip" title="Edit"><i class="fa fa-edit text-xs"></i></button>
                        <button onClick="deleteDataTindakan(`'. route('tindakan.update', $tindakan->id) . '`)" class="btn btn-xs btn-default btn-rounded rounded-lg text-danger pl-2 pr-2" data-toggle="tooltip" title="Delete"><i class="fa fa-trash text-xs"></i></button>
                    </center>
                </div>
                ';
            })
            ->rawColumns(['status_aktif', 'action'])
            ->make(true);

        return view('pelayanan-klinik.tindakan.index', compact('idklinik'));
    }

    public function store(Request $request)
    {
        $idklinik = auth()->user()->id_klinik;
        $kode = $request->kode;
        $nama_en = $request->nama_en;

        $validator = Validator::make($request->all(), [
            'kode' => [
                'required',
                'max:11',
                Rule::unique('tindakan')->where(function ($query) use ($idklinik, $kode) {
                    return $query->where('id_klinik', $idklinik)
                                 ->where('kode', $kode);
                }),
            ],
            'nama_en' => [
                'required',
                'max:255',
                Rule::unique('tindakan')->where(function ($query) use ($idklinik, $nama_en) {
                    return $query->where('id_klinik', $idklinik)
                                 ->where('nama_en', $nama_en);
                }),
            ],
            'nama_id' => 'nullable|max:255',
        ], 
        [
            'kode.required' => 'Kode Tindakan harus diisi.',
            'kode.unique' => 'Kode Tindakan sudah digunakan.',
            'kode.max' => 'Maksimal jumlah karakter untuk Kode Tindakan adalah 11 digit.',
            'nama_en.required' => 'Nama Tindakan (EN) harus diisi.',
            'nama_en.unique' => 'Nama Tindakan (EN) sudah digunakan.',
            'nama_en.max' => 'Maksimal jumlah karakter untuk Nama Tindakan (EN) adalah 255 digit.',
            'nama_id.max' => 'Maksimal jumlah karakter untuk Nama Tindakan (ID) adalah 255 digit.',
        ]);

        if ($validator->fails()) {
            return response()->json([
                'error' => $validator->errors()->all()
            ], 422);
        }

        try {
            $tindakan = new Tindakan();
            $tindakan->kode = $kode;
            $tindakan->id_klinik = $idklinik;
            $tindakan->nama_en = $request->nama_en;
            $tindakan->nama_id = $request->nama_id;
            $tindakan->status_aktif = $request->status_aktif;
            $tindakan->save();

            return response()->json([
                'success' => 'Data baru berhasil disimpan.',
            ]);
        } catch (\Exception $e) {
            return response()->json([
                'error' => [$e->getMessage()] 
            ], 500);
        }
    }

    public function update(Request $request, $id)
    {
        $tindakan = Tindakan::findOrFail($id);
        $idklinik = auth()->user()->id_klinik;
        $kodeLama = $tindakan->kode;
        $kodeBaru = $request->kode;
        $namaLama = $tindakan->nama_en;
        $namaBaru = $request->nama_en;
        $namaLamaUpper = strtoupper($namaLama);
        $namaBaruUpper = strtoupper($namaBaru);

        if ($kodeLama != $kodeBaru) {
            $validasiKode = Validator::make($request->all(), [
                'kode' => [
                    'required',
                    'max:11',
                    Rule::unique('tindakan')->where(function ($query) use ($idklinik, $kodeBaru) {
                        return $query->where('id_klinik', $idklinik)
                                     ->where('kode', $kodeBaru);
                    }),
                ],
            ], [
                'kode.required' => 'Kode Tindakan (EN) harus diisi.',
                'kode.unique' => 'Kode Tindakan (EN) sudah digunakan.',
                'kode.max' => 'Maksimal jumlah karakter untuk Kode Tindakan adalah 11 digit.',
            ]);

            if ($validasiKode->fails()) {
                return response()->json([
                    'error' => $validasiKode->errors()->all()
                ], 422);
            }
        }

        if ($namaLamaUpper != $namaBaruUpper) {
            $validasiNama = Validator::make($request->all(), [
                'nama_en' => [
                    'required',
                    'max:100',
                    Rule::unique('tindakan')->where(function ($query) use ($idklinik, $namaBaru) {
                        return $query->where('id_klinik', $idklinik)
                                     ->where('nama_en', $namaBaru);
                    }),
                ],
                'nama_id' => 'nullable|max:255',
            ], [
                'nama_en.required' => 'Nama Tindakan (EN) harus diisi.',
                'nama_en.unique' => 'Nama Tindakan (EN) sudah digunakan.',
                'nama_en.max' => 'Maksimal jumlah karakter untuk Nama Tindakan (EN) adalah 255 digit.',
                'nama_id.max' => 'Maksimal jumlah karakter untuk Nama Tindakan (ID) adalah 255 digit.',
            ]);

            if ($validasiNama->fails()) {
                return response()->json([
                    'error' => $validasiNama->errors()->all()
                ], 422);
            }
        }

        $tindakan->kode = $kodeBaru;
        $tindakan->nama_en = $namaBaru;
        $tindakan->nama_id = $request->nama_id;
        $tindakan->status_aktif = $request->status_aktif;

        $tindakan->update();

        return response()->json(['success' => 'Data berhasil diperbaharui'], 200);
    }

    public function show($id)
    {
        $tindakan = Tindakan::find($id);
        return response()->json($tindakan);
    }
    
    public function destroy($id)
    {
        Tindakan::find($id)->delete();
      
        return response()->json(['success'=>'Satuan Obat deleted successfully.']);
    }

